import BugFound from "./BugFound";
import ComputerScreen from "./ComputerScreen";
import { useState } from "react";
import "./styles/transition.css";

function App() {
  const [currentScreen, setCurrentScreen] = useState("bugFound");
  const [prevScreen, setPrevScreen] = useState(null);
  const [isTransitioning, setIsTransitioning] = useState(false);

  const handleScreenChange = (next) => {
    setPrevScreen(currentScreen); // 이전 화면 저장
    setCurrentScreen(next); // 다음 화면 지정
    setIsTransitioning(true); // 트랜지션 시작

    setTimeout(() => {
      setPrevScreen(null); // 이전 화면 제거
      setIsTransitioning(false); // 트랜지션 끝
    }, 600); // 애니메이션 시간에 맞춰 (fade-out + fade-in)
  };

  return (
    <div className="screen-wrapper">
      {prevScreen === "bugFound" && <BugFound className="fade-out" />}
      {prevScreen === "computerScreen" && (
        <ComputerScreen className="fade-out" />
      )}

      {currentScreen === "bugFound" && (
        <BugFound
          className={isTransitioning ? "fade-in" : ""}
          onNext={() => handleScreenChange("computerScreen")}
        />
      )}
      {currentScreen === "computerScreen" && (
        <ComputerScreen
          className={isTransitioning ? "fade-in" : ""}
          onNext={() => handleScreenChange("bugFound")}
        />
      )}
    </div>
  );
}

export default App;
