import { useState } from "react";
import logo from "./assets/images/uglyworld_logo.png";
import "./styles/startScreen.css";

function StartScreen({ onNext }) {
  const [clicked, setClicked] = useState(false);

  const handleClick = () => {
    console.log(clicked);
    if (clicked) return; // 중복 클릭 방지
    setClicked(true);
    console.log(clicked);
    setTimeout(() => {
      onNext();
    }, 600); // 애니메이션 끝난 뒤 전환
  };

  return (
    <div className="mobile" style={{ backgroundColor: "#731EE2" }}>
      <div
        className={`logo-wrapper ${clicked ? "clicked" : ""}`}
        onClick={handleClick}
      >
        <img src={logo} className="logo" alt="uglyworld logo" />
      </div>
    </div>
  );
}

export default StartScreen;
