import React, { useState, useEffect } from "react";
import Buttons from "../Buttons";
import Window from "../Window";

function QuizModal({
  level,
  question,
  hint,
  answer,
  value,
  onChange,
  onSubmit,
  onSkip,
  wrongAttempts,
  showHint,
  onClose,
  parentRef = {
    current: typeof document !== "undefined" ? document.body : null,
  },
}) {
  const [quizAnswer, setQuizAnswer] = useState(value || "");

  const handleSubmit = () => {
    if (quizAnswer.trim() === answer) {
      onSubmit(); // correct answer submission
      onClose && onClose(); // close modal on correct answer
    } else {
      onChange(quizAnswer); // update the value with the user's answer
    }
  };

  return (
    <div title="Quiz">
      <p>{question}</p>
      <input
        type="text"
        placeholder="정답을 입력하세요"
        value={quizAnswer}
        onChange={(e) => setQuizAnswer(e.target.value)}
        autoFocus
      />
      {wrongAttempts > 0 && <p style={{ color: "red" }}>틀렸습니다.</p>}
      {showHint && <p className="hint">힌트: {hint}</p>}
      <Buttons onClick={handleSubmit}>제출</Buttons>
      {wrongAttempts >= 3 && (
        <Buttons
          onClick={() => {
            onSkip(); // Skip the quiz after 3 wrong attempts
            onClose && onClose(); // Optionally close modal on skip
          }}
        >
          퀴즈 넘어가기
        </Buttons>
      )}
    </div>
  );
}

export default QuizModal;
