import React, { useState } from "react";
import Buttons from "../Buttons";

function QuizModal({ level, onCorrect, onSkip }) {
  const [answer, setAnswer] = useState("");

  const handleSubmit = () => {
    if (answer === "정답") {
      onCorrect();
    } else {
      alert("틀렸습니다!");
    }
  };

  return (
    <div className="quiz-modal">
      <h2>퀴즈 레벨 {level}</h2>
      <input
        type="text"
        value={answer}
        onChange={(e) => setAnswer(e.target.value)}
        placeholder="정답을 입력하세요"
      />
      <Buttons onClick={handleSubmit}>제출</Buttons>
      <Buttons onClick={onSkip}>스킵</Buttons>
    </div>
  );
}

export default QuizModal;
