import {
  collection,
  query,
  where,
  getDocs,
  doc,
  getDoc,
} from "firebase/firestore";
import { db } from "../../firebase";
import { useEffect, useState, useRef } from "react";
import Window from "../Window";
import Buttons from "../Buttons";
import "./minesweeper.css";
import quizData from "../../assets/data/quizzes.json";

function Minesweeper({
  level = 1,
  onCorrect,
  onClose,
  onSelfClose,
  onSuccess,
  solved,
  skippedCount,
  setSkippedCount,
  parentRef,
}) {
  const mineOrderRef = useRef([]);
  const [flagIndex, setFlagIndex] = useState(0);
  const [quizAnswer, setQuizAnswer] = useState("");
  const [quizOpen, setQuizOpen] = useState(false);
  const [quizId, setQuizId] = useState(level);

  const [grid, setGrid] = useState([]);
  const [mineCount, setMineCount] = useState(0);
  const [wrongAttempts, setWrongAttempts] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [skippedFlags, setSkippedFlags] = useState([]);
  const [resetKey, setResetKey] = useState(0);
  const [gameState, setGameState] = useState("angry"); // "angry" or "cool"
  const [skippedThisLevel, setSkippedThisLevel] = useState(false);

  function handleMinesweeperSuccess(level) {
    // Set quizId based on the current level (1 to 10)
    if (level <= 10 && quizId <= 10) {
      setQuizId(level); // Set quizId based on the current level
    }
    setBugRemovalIndex((prev) =>
      level === 4 ? bugCount : prev + Math.floor(BUG_COUNT / 8)
    );
    setSolvedLevels((prev) => [...new Set([...prev, level])]);
    setVisibleFolderIndex((prev) => Math.max(prev, level));
  }

  const handleSubmitanswer = async () => {
    const dirs = [-1, 0, 1];
    try {
      const data = quizData[level];
      if (data) {
        const correctAnswer = data.answer;

        if (quizAnswer.trim() === correctAnswer) {
          setGrid((prev) => {
            const newGrid = prev.map((r) => r.map((c) => ({ ...c })));
            const [cx, cy] = mineOrderRef.current[flagIndex];
            newGrid[cx][cy].cleared = true;
            newGrid[cx][cy].revealed = true;
            newGrid[cx][cy].flagged = false;

            dirs.forEach((dx) =>
              dirs.forEach((dy) => {
                if (dx || dy) {
                  const nx = cx + dx,
                    ny = cy + dy;
                  if (newGrid[nx]?.[ny] && !newGrid[nx][ny].mine) {
                    newGrid[nx][ny].revealed = true;
                  }
                }
              })
            );

            const nextIndex = flagIndex + 1;
            if (nextIndex < mineOrderRef.current.length) {
              const [nx, ny] = mineOrderRef.current[nextIndex];
              newGrid[nx][ny].flagged = true;
              dirs.forEach((dx) =>
                dirs.forEach((dy) => {
                  if (dx || dy) {
                    const rx = nx + dx,
                      ry = ny + dy;
                    if (newGrid[rx]?.[ry] && !newGrid[rx][ry].mine) {
                      newGrid[rx][ry].revealed = true;
                    }
                  }
                })
              );
            }
            return newGrid;
          });
          setFlagIndex((i) => i + 1);
          onCorrect && onCorrect();
          setQuizOpen(false);
        } else {
          setWrongAttempts((prev) => {
            const next = prev + 1;
            if (next >= 3) setShowHint(true);
            return next;
          });
        }
      } else {
        alert("Î¨∏Ï†ú Îç∞Ïù¥ÌÑ∞Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
      }
    } catch (error) {
      console.error("Ï†ïÎãµ ÌôïÏù∏ Ï§ë Ïò§Î•ò:", error);
    } finally {
      setQuizAnswer("");
    }
  };

  useEffect(() => {
    const size = level + 2; // grid size increases with level (7 to 13)
    const mines = level; // mine count equals level for now
    setMineCount(mines);

    const newGrid = Array.from({ length: size }, () =>
      Array.from({ length: size }, () => ({
        revealed: false,
        mine: false,
        flagged: false,
        cleared: false,
        adjacentMines: 0,
      }))
    );

    if (solved) {
      for (let i = 0; i < size; i++) {
        for (let j = 0; j < size; j++) {
          newGrid[i][j].cleared = true;
          newGrid[i][j].revealed = true;
        }
      }
      setGrid(newGrid);
      setGameState("cool");
      return;
    }

    // Build a fixed random order of mine coords
    const allCells = [];
    for (let i = 0; i < size; i++) {
      for (let j = 0; j < size; j++) {
        allCells.push([i, j]);
      }
    }
    const shuffled = allCells.sort(() => 0.5 - Math.random()).slice(0, mines);
    mineOrderRef.current = shuffled;
    setFlagIndex(0);

    // Place mines
    mineOrderRef.current.forEach(([x, y]) => {
      newGrid[x][y].mine = true;
    });

    // Calculate adjacent mines
    const dirs2 = [-1, 0, 1];
    for (let x = 0; x < size; x++) {
      for (let y = 0; y < size; y++) {
        if (newGrid[x][y].mine) continue;
        let count = 0;
        dirs2.forEach((dx) => {
          dirs2.forEach((dy) => {
            if (dx === 0 && dy === 0) return;
            const nx = x + dx;
            const ny = y + dy;
            if (
              nx >= 0 &&
              ny >= 0 &&
              nx < size &&
              ny < size &&
              newGrid[nx][ny].mine
            ) {
              count++;
            }
          });
        });
        newGrid[x][y].adjacentMines = count;
      }
    }

    // Flag the first mine in order and reveal its neighbors
    const [fx, fy] = mineOrderRef.current[0];
    newGrid[fx][fy].flagged = true;
    const dirs = [-1, 0, 1];
    dirs.forEach((dx) =>
      dirs.forEach((dy) => {
        if (dx || dy) {
          const nx = fx + dx,
            ny = fy + dy;
          if (newGrid[nx]?.[ny] && !newGrid[nx][ny].mine) {
            newGrid[nx][ny].revealed = true;
          }
        }
      })
    );

    setGrid(newGrid);
  }, [level, resetKey, solved]);

  const onCellClick = (row, col) => {
    setGrid((prevGrid) => {
      const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
      const clicked = newGrid[row][col];
      if (clicked.cleared) return newGrid;
      if (clicked.flagged && !clicked.cleared) {
        setWrongAttempts(0);
        setShowHint(false);
        setQuizOpen(true);
      }
      // All other clicks do nothing (don't reveal)
      return newGrid;
    });
  };

  useEffect(() => {
    setWrongAttempts(0);
    setShowHint(false);
  }, []);

  // switch to cool state when all flagged cells have been cleared (üß™)
  useEffect(() => {
    if (grid.length === 0) return;
    const allFlagsCleared = grid.every((row) =>
      row.every((cell) => {
        return !cell.flagged || cell.cleared;
      })
    );
    if (allFlagsCleared && !skippedThisLevel && gameState !== "hmm") {
      setGameState("cool");
      if (!solved && onSuccess) onSuccess();
    }
  }, [grid, gameState, skippedThisLevel]);

  return (
    <div className="minesweeper">
      <p>
        Level: {level}{" "}
        <div
          className="minesweeper-center-icon"
          onClick={() => {
            if (gameState === "angry") {
              // reset game
              setResetKey((k) => k + 1);
              setWrongAttempts(0);
              setShowHint(false);
            } else if (gameState === "cool" || gameState === "hmm") {
              if (onSelfClose) onSelfClose();
            }
          }}
        >
          {gameState === "angry"
            ? "üò°"
            : gameState === "cool"
            ? skippedThisLevel
              ? "üòê"
              : "üòé"
            : "üòê"}
        </div>{" "}
        Mines: {mineCount}
      </p>
      <div className="minesweeper-wrapper">
        <div className="minesweeper-grid" style={{ "--cols": grid.length }}>
          {grid.map((row, rowIndex) =>
            row.map((cell, colIndex) => (
              <div
                key={`${rowIndex}-${colIndex}`}
                className={`minesweeper-cell 
  ${cell.revealed ? "revealed" : ""}
  ${cell.flagged ? "flagged" : ""}
  ${
    cell.revealed && (cell.cleared || cell.adjacentMines > 0)
      ? "sunken"
      : "raised"
  }`}
                onClick={() => onCellClick(rowIndex, colIndex)}
              >
                {cell.revealed
                  ? cell.cleared
                    ? "üß™"
                    : cell.mine
                    ? "üí£"
                    : cell.adjacentMines > 0
                    ? cell.adjacentMines
                    : ""
                  : skippedFlags.includes(`${rowIndex}-${colIndex}`)
                  ? "‚ùå"
                  : cell.flagged
                  ? ""
                  : ""}
              </div>
            ))
          )}
        </div>
      </div>
      {quizOpen && (
        <div className="quiz-modal">
          <p>{quizData[quizId - 1]?.question}</p>{" "}
          {/* Display quiz based on quizId */}
          <input
            type="text"
            placeholder="Ï†ïÎãµÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            value={quizAnswer}
            onChange={(e) => setQuizAnswer(e.target.value)}
          />
          {wrongAttempts > 0 && wrongAttempts < 3 && (
            <p style={{ color: "red" }}>ÌãÄÎ†∏ÏäµÎãàÎã§!</p>
          )}
          {showHint && (
            <p className="hint">ÌûåÌä∏: {quizData[quizId - 1]?.hint}</p>
          )}
          <Buttons onClick={handleSubmitanswer}>Ï†úÏ∂ú</Buttons>
          {wrongAttempts >= 3 && (
            <Buttons
              onClick={() => {
                setQuizOpen(false);
                let didSkip = false;

                const updatedGrid = grid.map((row, i) =>
                  row.map((cell, j) => {
                    if (!didSkip && cell.flagged && !cell.cleared) {
                      didSkip = true;
                      setSkippedFlags((prev) => [...prev, `${i}-${j}`]);
                      setGameState("hmm");
                      setSkippedThisLevel(true);
                      if (setSkippedCount) setSkippedCount((prev) => prev + 1);
                      return {
                        ...cell,
                        flagged: false,
                        cleared: true,
                        revealed: true,
                      };
                    }
                    return cell;
                  })
                );

                setGrid(updatedGrid);

                if (didSkip && typeof onSuccess === "function") {
                  onSuccess();
                }
              }}
            >
              ÌÄ¥Ï¶à ÎÑòÏñ¥Í∞ÄÍ∏∞
            </Buttons>
          )}
        </div>
      )}
    </div>
  );
