import { useEffect, useState, useRef } from "react";
import "./minesweeper.css";

// Minesweeper에서 QuizModal 관련 로직을 제거하고, onQuizOpen 이벤트를 호출하도록 수정
function Minesweeper({ level = 1, onCorrect, onSkip, onQuizOpen, solved }) {
  const [grid, setGrid] = useState([]);
  const [flagIndex, setFlagIndex] = useState(0);

  useEffect(() => {
    const size = level + 2;
    const newGrid = Array.from({ length: size }, () =>
      Array.from({ length: size }, () => ({
        revealed: false,
        flagged: false,
        cleared: false,
      }))
    );

    newGrid[0][0].flagged = true; // 첫 깃발 설정
    setGrid(newGrid);
  }, [level]);

  const handleCellClick = (row, col) => {
    setGrid((prevGrid) => {
      const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
      const clicked = newGrid[row][col];
      if (clicked.flagged && !clicked.cleared) {
        onQuizOpen(level, `${row}-${col}`);
      }
      return newGrid;
    });
  };

  return (
    <div className="minesweeper">
      <div className="minesweeper-grid" style={{ "--cols": grid.length }}>
        {grid.map((row, rowIndex) =>
          row.map((cell, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              className={`minesweeper-cell ${cell.flagged ? "flagged" : ""}`}
              onClick={() => handleCellClick(rowIndex, colIndex)}
            >
              {cell.flagged ? "🚩" : ""}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Minesweeper;
