import { useEffect, useState, useRef } from "react";
import Minesweeper from "../../components/Minesweeper/Minesweeper";

// 로그인용
import { useAgent } from "../../contexts/AgentContext";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../../firebase";
import { hashPassword } from "../../utils/hash";

import "../../styles/layout.css";
import "../../styles/transition.css";
import "./mainScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";
import Submits from "../../components/Submits";

// folder
import activatedImg from "../../assets/images/activated-folder.png";
import inactivatedImg from "../../assets/images/inactivated-folder.png";

function MainScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  const [clickDisabled, setClickDisabled] = useState(false);
  const { setAgent, skippedCount, setSkippedCount } = useAgent();
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");
  const [windows, setWindows] = useState([]);
  const nextWindowId = useRef(0);
  const [visibleFolderIndex, setVisibleFolderIndex] = useState(0);
  const [bugRemovalIndex, setBugRemovalIndex] = useState(0);
  const [solvedLevels, setSolvedLevels] = useState([]);
  // Quiz modal state
  const [quizData, setQuizData] = useState(null);
  const [quizAnswer, setQuizAnswer] = useState("");
  const [quizWindowId, setQuizWindowId] = useState(null);
  const [quizWrongAttempts, setQuizWrongAttempts] = useState(0);
  const [quizShowHint, setQuizShowHint] = useState(false);

  const bugCount = 150;
  const bugDataRef = useRef(
    Array.from({ length: bugCount }, () => ({
      top: Math.random() * 90,
      left: Math.random() * 90,
      size: 1.5 + Math.random() * 1.5,
    }))
  );

  const bugs = bugDataRef.current
    .map((data, i) => {
      if (i < bugRemovalIndex) return null;
      return (
        <span
          key={i}
          className="main-screen__bug-text"
          style={{
            top: `${data.top}%`,
            left: `${data.left}%`,
            fontSize: `${data.size}vh`,
          }}
        >
          BUG
        </span>
      );
    })
    .filter(Boolean);

  async function handleLoginSubmit(e) {
    e.preventDefault();
    const hashed = await hashPassword(password);

    const q = query(
      collection(db, "agents"),
      where("passwordHash", "==", hashed)
    );
    const snapshot = await getDocs(q);

    if (!snapshot.empty) {
      const doc = snapshot.docs[0];
      const data = doc.data();
      setAgent({
        id: doc.id,
        progress: data.progress ?? 0,
        passwordHash: hashed,
      });
      setLoginError("");
      handleClick();
    } else {
      setLoginError("요원코드가 올바르지 않습니다.");
      handleClose();
    }
  }

  const screenRef = useRef(null);

  const se_error = new Audio(error);
  const se_shutdown = new Audio(shutdown);

  function createWindowContent(label) {
    return {
      title: "UglyWorld in_BUG",
      content: <div>{label} 내용입니다.</div>,
    };
  }

  useEffect(() => {
    const fadeTimeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    return () => {
      clearTimeout(fadeTimeout);
    };
  }, []);

  function handleClick() {
    if (clickDisabled) return;
    const id = nextWindowId.current++;
    const { title, content } = createWindowContent("첫 번째 창");
    setWindows((prev) => [...prev, { id, title, content }]);
    se_error.play();
  }

  function handleClose(id) {
    setWindows((prev) => prev.filter((win) => win.id !== id));
  }

  function handleMinesweeperSuccess(level) {
    setSolvedLevels((prevSolved) => {
      if (prevSolved.includes(level)) return prevSolved;

      // only update if not already solved
      setBugRemovalIndex((prev) => {
        const step = level === 4 ? bugCount : Math.floor(bugCount / 4);
        return Math.min(prev + step, bugCount);
      });
      setVisibleFolderIndex((prev) => Math.max(prev, level));

      return [...prevSolved, level];
    });
  }

  // Quiz modal logic
  function openQuizWindow(level, cell) {
    const id = nextWindowId.current++;
    setQuizWindowId(id);
    setQuizWrongAttempts(0);
    setQuizShowHint(false);
    const quizzes = require("../../assets/data/quizzes.json");
    setQuizData({
      ...quizzes[level],
      level,
      cell,
    });
    setWindows((prev) => [
      ...prev,
      {
        id,
        title: "퀴즈",
        content: (
          <div>
            <p>{quizzes[level].question}</p>
            <input
              type="text"
              placeholder="정답을 입력하세요"
              value={quizAnswer}
              onChange={(e) => setQuizAnswer(e.target.value)}
            />
            {quizWrongAttempts > 0 && (
              <p style={{ color: "red" }}>틀렸습니다.</p>
            )}
            {quizShowHint && (
              <p className="hint">힌트: {quizzes[level].hint}</p>
            )}
            <Buttons onClick={handleQuizSubmit}>제출</Buttons>
            {quizWrongAttempts >= 3 && (
              <Buttons onClick={handleSkipQuiz}>퀴즈 넘어가기</Buttons>
            )}
          </div>
        ),
      },
    ]);
  }

  function handleQuizSubmit() {
    if (!quizData) return;
    if (quizAnswer.trim() === quizData.answer) {
      setQuizAnswer("");
      setQuizWindowId(null);
      handleMinesweeperSuccess(quizData.level);
      setWindows((prev) => prev.filter((win) => win.id !== quizWindowId));
    } else {
      setQuizWrongAttempts((prev) => {
        const next = prev + 1;
        if (next >= 3) setQuizShowHint(true);
        return next;
      });
    }
  }

  function handleSkipQuiz() {
    if (!quizData) return;
    setSkippedCount((prev) => prev + 1);
    setQuizAnswer("");
    setQuizWindowId(null);
    setQuizShowHint(false);
    handleMinesweeperSuccess(quizData.level);
    setWindows((prev) => prev.filter((win) => win.id !== quizWindowId));
  }

  function handleFolderClick(label, level) {
    const id = nextWindowId.current++;
    const isSolved = solvedLevels.includes(level);
    setWindows((prev) => [
      ...prev,
      {
        id,
        title: "UglyWorld in_BUG",
        content: (
          <Minesweeper
            level={level}
            parentRef={screenRef}
            onSelfClose={() => handleClose(id)}
            onSuccess={() => handleMinesweeperSuccess(level)}
            onSkipSuccess={() => handleMinesweeperSuccess(level)}
            solved={isSolved}
            skippedCount={skippedCount}
            setSkippedCount={setSkippedCount}
            openQuizWindow={(cell) => openQuizWindow(level, cell)}
          />
        ),
      },
    ]);
    se_error.play();
  }

  return (
    <div className={`mobile main-screen pre-fade ${fadeIn}`}>
      <div className="main-screen__bug-background">
        {bugs}
        <div className="main-screen__folders">
          {["프로그램 설치파일", "보안패치", "바이러스 샘플", "비밀 로그"].map(
            (label, index) => {
              const isActive = index <= visibleFolderIndex;
              return (
                <div key={index} className="folder-wrapper">
                  <button
                    className="folder-button"
                    onClick={() =>
                      isActive && handleFolderClick(label, index + 1)
                    }
                    disabled={!isActive}
                  >
                    <img
                      src={isActive ? activatedImg : inactivatedImg}
                      alt={`${label} folder`}
                      className="folder-icon"
                    />
                  </button>
                  <span>{label}</span>
                </div>
              );
            }
          )}
        </div>
        <div className="main-screen__screen" ref={screenRef}>
          {windows.map(({ id, title, content }) => (
            <Window
              key={id}
              title={title}
              parentRef={screenRef}
              onClose={() => handleClose(id)}
              isActive="active"
            >
              {content}
            </Window>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainScreen;
