import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";

// ✅ 창 목록을 함수로 분리
const getDefaultWindows = (handleClick) => [
  {
    title: "UglyWorld in_BUG",
    content: (
      <>
        <span>요원이십니까?</span>
        <div>
          <Buttons onClick={handleClick}>예</Buttons>
          <Buttons>아니오</Buttons>
        </div>
      </>
    ),
  },
  {
    title: "UglyWorld in_BUG",
    content: (
      <>
        <span>요원 코드를 입력해주세요.</span>
        <input type="password" />
        <input type="submit" value="로그인" />
      </>
    ),
  },
  {
    title: "UglyWorld in_BUG",
    content: (
      <>
        <button className="vaccine-alert" onClick={handleClick}>
          <div className="vaccine-alert__start">START</div>
          <div className="vaccine-alert__vaccinate">VACCINATE</div>
        </button>
      </>
    ),
  },
  {
    title: "UglyWorld in_BUG",
    content: (
      <>
        백신 프로그램을 가동합니다. <br />
        강제종료하지 마세요.
        <br />
        (잠금화면 포함)
      </>
    ),
  },
];

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  const [windowList, setWindowList] = useState([]);
  const [clickDisabled, setClickDisabled] = useState(false);
  const screenRef = useRef(null);

  const se_error = new Audio(error);
  const se_shutdown = new Audio(shutdown);

  // handleClick은 defaultWindows 호출 전에 정의되어야 함
  const handleClick = () => {
    if (clickDisabled) return;

    const defaultWindows = getDefaultWindows(handleClick);

    if (windowList.length < defaultWindows.length) {
      setWindowList((prev) => [...prev, defaultWindows[prev.length]]);
      se_error.play();
    } else {
      setClickDisabled(true);
      se_error.play();

      setTimeout(() => {
        setWindowList((prev) => prev.slice(1));

        const removeInterval = setInterval(() => {
          setWindowList((prev) => {
            if (prev.length > 0) {
              return prev.slice(1);
            } else {
              clearInterval(removeInterval);
              setTimeout(() => {
                se_shutdown.play();
                onNext && onNext();
              }, 300);
              return [];
            }
          });
        }, 50);
      }, 1000);
    }
  };

  const handleClose = () => {
    se_error.play();
    if (windowList.length > 0) {
      const lastWindow = windowList[windowList.length - 1];
      setWindowList((prev) => [...prev, lastWindow]);
      console.log(windowList);
    }
  };

  useEffect(() => {
    const defaultWindows = getDefaultWindows(handleClick);

    const timeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    const timer = setTimeout(() => {
      if (windowList.length === 0) {
        setWindowList([defaultWindows[0]]);
        se_error.play();
      }
    }, 1000);

    return () => {
      clearTimeout(timeout);
      clearTimeout(timer);
    };
  }, [windowList]);

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" ref={screenRef}>
        {windowList.map((win, index) => (
          <Window
            key={index}
            title={win.title}
            parentRef={screenRef}
            onClose={handleClose}
          >
            {win.content}
          </Window>
        ))}
      </div>
    </div>
  );
}

export default ComputerScreen;
