import { useEffect, useRef, useState } from "react";
import QuizModal from "./QuizModal/QuizModal";
import "./window.css";

function Window({
  parentRef,
  title,
  children,
  onClick,
  onClose,
  isActive = "",
}) {
  const windowRef = useRef(null);
  const [pos, setPos] = useState({ top: 0, left: 0 });

  // Drag state and offset
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState(null);
  const initialPos = useRef(null);

  useEffect(() => {
    if (!parentRef?.current || !windowRef.current) return;

    const parentRect = parentRef.current.getBoundingClientRect();
    const windowRect = windowRef.current.getBoundingClientRect();

    // 안전 여유 (px)
    const margin = 20;
    const maxTop = parentRect.height - windowRect.height - margin;
    const maxLeft = parentRect.width - windowRect.width - margin;

    // 음수 방지
    const safeTop = Math.max(0, maxTop);
    const safeLeft = Math.max(0, maxLeft);

    // 랜덤 배치 (부모 안)
    const randomTop = margin + Math.floor(Math.random() * safeTop);
    const randomLeft = margin + Math.floor(Math.random() * safeLeft);

    setPos({ top: randomTop, left: randomLeft });
    if (windowRef.current) {
      windowRef.current.style.top = `${randomTop}px`;
      windowRef.current.style.left = `${randomLeft}px`;
    }
  }, []);

  // Mouse event handlers for dragging
  const handleMouseDown = (e) => {
    e.preventDefault();
    setIsDragging(true);
    console.log("Drag started at", e.clientX, e.clientY);
    setDragStart({ x: e.clientX, y: e.clientY });
    initialPos.current = { ...pos };
  };

  const handleMouseMove = (e) => {
    if (
      !isDragging ||
      !dragStart ||
      !initialPos.current ||
      !parentRef?.current ||
      !windowRef?.current
    )
      return;

    const dx = e.clientX - dragStart.x;
    const dy = e.clientY - dragStart.y;

    const parentRect = parentRef.current.getBoundingClientRect();
    const newLeft = initialPos.current.left + dx;
    const newTop = initialPos.current.top + dy;

    const clampedLeft = Math.max(
      0,
      Math.min(newLeft, parentRect.width - windowRef.current.offsetWidth)
    );
    const clampedTop = Math.max(
      0,
      Math.min(newTop, parentRect.height - windowRef.current.offsetHeight)
    );

    console.log("Dragging to", clampedLeft, clampedTop);
    windowRef.current.style.left = `${clampedLeft}px`;
    windowRef.current.style.top = `${clampedTop}px`;
    if (clampedTop !== pos.top || clampedLeft !== pos.left) {
      setPos({ top: clampedTop, left: clampedLeft });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    console.log("Drag ended");
  };

  const handleTouchStart = (e) => {
    if (e.touches.length === 1) {
      setIsDragging(true);
      console.log(
        "Touch drag started at",
        e.touches[0].clientX,
        e.touches[0].clientY
      );
      setDragStart({ x: e.touches[0].clientX, y: e.touches[0].clientY });
      initialPos.current = { ...pos };
    }
  };

  const handleTouchMove = (e) => {
    e.preventDefault();
    if (
      !isDragging ||
      !dragStart ||
      !initialPos.current ||
      !parentRef?.current ||
      !windowRef?.current
    )
      return;

    const dx = e.touches[0].clientX - dragStart.x;
    const dy = e.touches[0].clientY - dragStart.y;

    const parentRect = parentRef.current.getBoundingClientRect();
    const newLeft = initialPos.current.left + dx;
    const newTop = initialPos.current.top + dy;

    const clampedLeft = Math.max(
      0,
      Math.min(newLeft, parentRect.width - windowRef.current.offsetWidth)
    );
    const clampedTop = Math.max(
      0,
      Math.min(newTop, parentRect.height - windowRef.current.offsetHeight)
    );

    console.log("Touch dragging to", clampedLeft, clampedTop);
    windowRef.current.style.left = `${clampedLeft}px`;
    windowRef.current.style.top = `${clampedTop}px`;
    if (clampedTop !== pos.top || clampedLeft !== pos.left) {
      setPos({ top: clampedTop, left: clampedLeft });
    }
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
    console.log("Touch drag ended");
  };

  useEffect(() => {
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
    document.addEventListener("touchmove", handleTouchMove, { passive: false });
    document.addEventListener("touchend", handleTouchEnd);
    return () => {
      document.removeEventListener("mousemove", handleMouseMove);
      document.removeEventListener("mouseup", handleMouseUp);
      document.removeEventListener("touchmove", handleTouchMove, {
        passive: false,
      });
      document.removeEventListener("touchend", handleTouchEnd);
    };
  }, []);

  return (
    <div
      ref={windowRef}
      className={`window ${isActive}`}
      style={{ top: pos.top, left: pos.left, position: "absolute" }}
      onClick={onClick}
    >
      <div
        className="window-header"
        onMouseDown={handleMouseDown}
        onTouchStart={handleTouchStart}
      >
        <span className="window-title">{title}</span>
        <button
          className="window-close"
          onClick={(e) => {
            e.stopPropagation();
            onClose && onClose();
          }}
        >
          X
        </button>
      </div>
      <div className="window-body">{children}</div>
    </div>
  );
}

export default Window;
