import { useState } from "react";
import "./styles/layout.css";
import "./styles/startScreen.css";

// images
import logo from "./assets/images/uglyworld_logo.png";

function StartScreen({ onNext }) {
  const [clicked, setClicked] = useState(false);

  const handleClick = () => {
    setClicked(true);
    setTimeout(() => {
      onNext();
    }, 600);
  };

  return (
    <div
      className="mobile"
      onClick={onNext}
      style={{ backgroundColor: "#731EE2" }}
    >
      <img
        src={logo}
        className={`logo ${clicked ? "clicked" : ""}`}
        onClick={handleClick}
        alt="uglyworld logo"
      />
    </div>
  );
}

export default StartScreen;
