//import styles from "./App.module.css";
import BugFound from "./BugFound";
import { useState } from "react";
import ComputerScreen from "./ComputerScreen";

// styles
import "./styles/transition.css";

function App() {
  const [screen, setScreen] = useState("bugFound");
  const [fadeIn, setFadeIn] = useState(true);

  const handleScreenChange = (nextScreen) => {
    setFadeIn(true); // 먼저 opacity 0
    setTimeout(() => {
      setScreen(nextScreen); // 실제 화면 전환
      setFadeIn(false); // 다시 fade-in
    }, 100); // 전환 딜레이 (0.1초)
  };

  let currentScreen;
  switch (screen) {
    case "bugFound":
      currentScreen = (
        <BugFound onNext={() => handleScreenChange("computerScreen")} />
      );
      break;
    case "computerScreen":
      currentScreen = <ComputerScreen onNext={() => setScreen("bugFound")} />;
      break;
    default:
      currentScreen = <div>404</div>;
  }

  return (
    <div className={`${fadeIn ? "fade-enter-active" : "fade-enter"}`}>
      {currentScreen}
    </div>
  );
}

export default App;
