import { useEffect, useState } from "react";
import styles from "./App.module.css";
import backgroundImage from "./assets/urdy.png";

function App() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    // 1초마다 현재 시간 업데이트
    const timer = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  return (
    <div className={styles.App}>
      <div
        className={styles.mobile}
        style={{ backgroundImage: `url(${backgroundImage})` }}
      >
        <div className={styles.overlay}>
          <div className={styles.warning}>밀레니엄 버그 발견</div>
          <div className={styles.date}>{time.toLocaleDateString()}</div>
          <div className={styles.time}>{time.toLocaleTimeString()}</div>
        </div>
        <div className={styles.bugName}>URDY</div>
      </div>
    </div>
  );
}

export default App;
