import {
  collection,
  query,
  where,
  getDocs,
  doc,
  getDoc,
} from "firebase/firestore";
import { db } from "../../firebase";
import { useEffect, useState, useRef } from "react";
import Window from "../Window";
import Buttons from "../Buttons";
import QuizModal from "../QuizModal/QuizModal";
import "./minesweeper.css";
import quizData from "../../assets/data/quizzes.json";

function Minesweeper({
  level = 1,
  onCorrect,
  onClose,
  onSelfClose,
  onSuccess,
  solved,
  skippedCount,
  setSkippedCount,
  parentRef,
  onFlagClick,
  solvedFlags = [],
}) {
  const mineOrderRef = useRef([]);
  const appliedSolvedFlagsRef = useRef([]);
  const [flagIndex, setFlagIndex] = useState(0);

  const [grid, setGrid] = useState([]);
  const [mineCount, setMineCount] = useState(0);
  const [skippedFlags, setSkippedFlags] = useState([]);
  const [resetKey, setResetKey] = useState(0);
  const [gameState, setGameState] = useState("angry"); // "angry" or "cool"
  const [skippedThisLevel, setSkippedThisLevel] = useState(false);

  const [quizOpen, setQuizOpen] = useState(false);
  const [quizAnswer, setQuizAnswer] = useState("");
  const [flaggedCell, setFlaggedCell] = useState(null); // Store the flagged cell information
  const [quizWrongAttempts, setQuizWrongAttempts] = useState(0);
  const [quizShowHint, setQuizShowHint] = useState(false);

  useEffect(() => {
    const size = level + 2; // grid size increases with level (7 to 13)
    const mines = level; // mine count equals level for now
    setMineCount(mines);

    const newGrid = Array.from({ length: size }, () =>
      Array.from({ length: size }, () => ({
        revealed: false,
        mine: false,
        flagged: false,
        cleared: false,
        adjacentMines: 0,
      }))
    );

    if (solved) {
      for (let i = 0; i < size; i++) {
        for (let j = 0; j < size; j++) {
          newGrid[i][j].cleared = true;
          newGrid[i][j].revealed = true;
        }
      }
      setGrid(newGrid);
      setGameState("cool");
      return;
    }

    // Build a fixed random order of mine coords
    const allCells = [];
    for (let i = 0; i < size; i++) {
      for (let j = 0; j < size; j++) {
        allCells.push([i, j]);
      }
    }
    const shuffled = allCells.sort(() => 0.5 - Math.random()).slice(0, mines);
    mineOrderRef.current = shuffled;
    setFlagIndex(0);

    // Place mines
    mineOrderRef.current.forEach(([x, y]) => {
      newGrid[x][y].mine = true;
    });

    // Calculate adjacent mines
    const dirs2 = [-1, 0, 1];
    for (let x = 0; x < size; x++) {
      for (let y = 0; y < size; y++) {
        if (newGrid[x][y].mine) continue;
        let count = 0;
        dirs2.forEach((dx) => {
          dirs2.forEach((dy) => {
            if (dx === 0 && dy === 0) return;
            const nx = x + dx;
            const ny = y + dy;
            if (
              nx >= 0 &&
              ny >= 0 &&
              nx < size &&
              ny < size &&
              newGrid[nx][ny].mine
            ) {
              count++;
            }
          });
        });
        newGrid[x][y].adjacentMines = count;
      }
    }

    // Flag the first mine in order and reveal its neighbors
    const [fx, fy] = mineOrderRef.current[0];
    newGrid[fx][fy].flagged = true;
    const dirs = [-1, 0, 1];
    dirs.forEach((dx) =>
      dirs.forEach((dy) => {
        if (dx || dy) {
          const nx = fx + dx,
            ny = fy + dy;
          if (newGrid[nx]?.[ny] && !newGrid[nx][ny].mine) {
            newGrid[nx][ny].revealed = true;
          }
        }
      })
    );

    setGrid(newGrid);
  }, [level, resetKey, solved]);

  const onCellClick = (row, col) => {
    const clicked = grid[row][col];
    if (clicked.flagged) {
      // Open quiz if the cell is flagged
      setFlaggedCell({ row, col });
      setQuizOpen(true);
      setQuizWrongAttempts(0);
      setQuizShowHint(false);
      setQuizAnswer("");
    }
  };

  function handleQuizSubmit() {
    if (!quizData) return;
    const correctAnswer = quizData[level]?.answer;
    if (quizAnswer.trim() === correctAnswer) {
      // Answer is correct: Reveal the flagged cell and handle success
      setGrid((prevGrid) => {
        const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
        newGrid[flaggedCell.row][flaggedCell.col].revealed = true;
        newGrid[flaggedCell.row][flaggedCell.col].cleared = true;
        return newGrid;
      });
      setQuizAnswer("");
      setQuizOpen(false);
      onCorrect && onCorrect();
      setFlaggedCell(null);
    } else {
      alert("Incorrect answer. Try again!");
      setQuizWrongAttempts((prev) => prev + 1);
      if (quizWrongAttempts >= 3) setQuizShowHint(true);
    }
  }

  function handleSkipQuiz() {
    setSkippedCount((prev) => prev + 1);
    setQuizAnswer("");
    setQuizOpen(false);
    setFlaggedCell(null);
    setQuizShowHint(false);
    handleMinesweeperSuccess();
  }
  const handleSubmitAnswer = () => {
    const correctAnswer = quizData[level]?.answer;
    if (quizAnswer.trim() === correctAnswer) {
      // Reveal the flagged cell if correct
      setGrid((prevGrid) => {
        const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
        newGrid[flaggedCell.row][flaggedCell.col].revealed = true;
        newGrid[flaggedCell.row][flaggedCell.col].cleared = true;
        return newGrid;
      });
      onCorrect && onCorrect();
      setQuizOpen(false);
      setQuizAnswer("");
      setFlaggedCell(null);
    } else {
      alert("Incorrect Answer");
    }
  };

  // switch to cool state when all flagged cells have been cleared (🧪)
  useEffect(() => {
    if (grid.length === 0) return;
    const allFlagsCleared = grid.every((row) =>
      row.every((cell) => {
        return !cell.flagged || cell.cleared;
      })
    );
    if (allFlagsCleared && !skippedThisLevel && gameState !== "hmm") {
      setGameState("cool");
      if (!solved && onSuccess) onSuccess();
    }
  }, [grid, gameState, skippedThisLevel]);

  useEffect(() => {
    console.log("✅ Minesweeper received new solvedFlags:", solvedFlags);
  }, [solvedFlags]);

  // Reveal and clear solved flag cells in the grid when solvedFlags updates
  useEffect(() => {
    console.log("Minesweeper useEffect for solvedFlags runs");
    if (!grid.length || !solvedFlags.length) return;

    const newFlags = solvedFlags.filter(
      (f) =>
        !appliedSolvedFlagsRef.current.some(
          (prev) =>
            prev.level === f.level &&
            prev.cell?.row === f.cell?.row &&
            prev.cell?.col === f.cell?.col
        )
    );

    console.log("🧪 [useEffect] 새로 들어온 solvedFlags:", newFlags);

    if (newFlags.length === 0) return;

    setGrid((prevGrid) => {
      const newGrid = prevGrid.map((row) => row.map((cell) => ({ ...cell })));

      newFlags.forEach((f) => {
        const { row, col } = f.cell;
        if (
          typeof row === "number" &&
          typeof col === "number" &&
          newGrid[row] &&
          newGrid[row][col]
        ) {
          newGrid[row][col].revealed = true;
          newGrid[row][col].cleared = true;
          console.log(
            `🧪 셀 [${row}, ${col}] revealed/cleared 설정됨`,
            newGrid[row][col]
          );
        }
      });

      return newGrid;
    });

    appliedSolvedFlagsRef.current = [
      ...appliedSolvedFlagsRef.current,
      ...newFlags,
    ];
  }, [solvedFlags, grid.length, level]);

  return (
    <div className="minesweeper">
      <div className="minesweeper-header">
        <p>
          Level: {level} Mines: {mineCount}
        </p>
        <div
          className="minesweeper-center-icon"
          onClick={() => {
            if (gameState === "angry") {
              setResetKey((k) => k + 1);
            } else if (gameState === "cool" || gameState === "hmm") {
              if (onSelfClose) onSelfClose();
            }
          }}
        >
          {gameState === "angry"
            ? "😡"
            : gameState === "cool"
            ? skippedThisLevel
              ? "😐"
              : "😎"
            : "😐"}
        </div>
      </div>
      <div className="minesweeper-wrapper">
        <div className="minesweeper-grid" style={{ "--cols": grid.length }}>
          {grid.map((row, rowIndex) =>
            row.map((cell, colIndex) => (
              <div
                key={`${rowIndex}-${colIndex}`}
                className={`minesweeper-cell 
  ${cell.revealed ? "revealed" : ""}
  ${cell.flagged ? "flagged" : ""}
  ${
    cell.revealed && (cell.cleared || cell.adjacentMines > 0)
      ? "sunken"
      : "raised"
  }`}
                onClick={() => onCellClick(rowIndex, colIndex)}
              >
                {(() => {
                  const isSolvedFlag = solvedFlags.some(
                    (f) =>
                      f.level === level &&
                      f.cell &&
                      f.cell.row === rowIndex &&
                      f.cell.col === colIndex
                  );
                  return cell.revealed
                    ? cell.cleared
                      ? "🧪"
                      : cell.mine
                      ? "💣"
                      : cell.adjacentMines > 0
                      ? cell.adjacentMines
                      : ""
                    : skippedFlags.includes(`${rowIndex}-${colIndex}`)
                    ? "❌"
                    : isSolvedFlag
                    ? "🧪"
                    : cell.flagged
                    ? "🚩"
                    : "";
                })()}
              </div>
            ))
          )}
        </div>
      </div>
      {quizOpen && (
        <QuizModal
          question={quizData[level]?.question}
          hint={quizShowHint ? quizData[level]?.hint : ""}
          answer={quizAnswer}
          value={quizAnswer}
          onChange={setQuizAnswer}
          onSubmit={handleQuizSubmit}
          onSkip={handleSkipQuiz}
        />
      )}
    </div>
  );
}

export default Minesweeper;
