import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  // windowIndices 배열에는 defaultWindows의 인덱스들이 저장됨
  // 예를 들어, [0, 1, 1]이면 defaultWindows[0] 창과 defaultWindows[1] 창이 두 개 보여짐
  const [windowIndices, setWindowIndices] = useState([]);
  const [clickDisabled, setClickDisabled] = useState(false);
  const screenRef = useRef(null);

  const se_error = new Audio(error);
  const se_shutdown = new Audio(shutdown);

  // 기본 창 목록 (인덱스 0 ~ 3)
  const defaultWindows = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원이십니까?</span>
          <div>
            <Buttons onClick={handleClick}>예</Buttons>
            <Buttons>아니오</Buttons>
          </div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원 코드를 입력해주세요.</span>
          <input type="password" />
          <input type="submit" value="로그인" />
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <button className="vaccine-alert" onClick={handleClick}>
            <div className="vaccine-alert__start">START</div>
            <div className="vaccine-alert__vaccinate">VACCINATE</div>
          </button>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          백신 프로그램을 가동합니다. <br />
          강제종료하지 마세요.
          <br />
          (잠금화면 포함)
        </>
      ),
    },
  ];

  // 초기 fade-in 효과와 첫 창(인덱스 0) 생성
  useEffect(() => {
    const fadeTimeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    const initTimeout = setTimeout(() => {
      setWindowIndices([0]); // 첫 창은 defaultWindows[0]
      se_error.play();
    }, 1000);

    return () => {
      clearTimeout(fadeTimeout);
      clearTimeout(initTimeout);
    };
  }, []);

  // handleClick: "예" 버튼 등에서 호출되어 창을 하나씩 추가하다가,
  // defaultWindows의 마지막 창(인덱스 3)이 이미 보여진 상태면 자동 제거 시퀀스를 시작함
  function handleClick() {
    if (clickDisabled) return;
    const lastIndex = windowIndices[windowIndices.length - 1];
    if (lastIndex < defaultWindows.length - 1) {
      setWindowIndices((prev) => [...prev, lastIndex + 1]);
      se_error.play();
    } else {
      // 마지막 창(인덱스 3)이면 제거 시퀀스로 전환
      setClickDisabled(true);
      se_error.play();
      setTimeout(() => {
        // 우선 첫 창부터 하나씩 제거
        setWindowIndices((prev) => prev.slice(1));
        const removalInterval = setInterval(() => {
          setWindowIndices((prev) => {
            if (prev.length > 0) {
              return prev.slice(1);
            } else {
              clearInterval(removalInterval);
              setTimeout(() => {
                se_shutdown.play();
                onNext && onNext();
              }, 300);
              return [];
            }
          });
        }, 50);
      }, 1000);
    }
  }

  // handleClose: x 버튼 클릭 시, 현재 가장 큰 인덱스(즉, 가장 마지막 창)의 내용을 그대로 복제하여 추가
  function handleClose() {
    if (clickDisabled) return;
    se_error.play();
    if (windowIndices.length > 0) {
      const lastIndex = windowIndices[windowIndices.length - 1];
      setWindowIndices((prev) => [...prev, lastIndex]);
    }
  }

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" ref={screenRef}>
        {windowIndices.map((idx, i) => {
          const win = defaultWindows[idx];
          if (!win) return null;
          return (
            <Window
              key={i}
              title={win.title}
              parentRef={screenRef}
              onClose={handleClose}
            >
              {win.content}
            </Window>
          );
        })}
      </div>
    </div>
  );
}

export default ComputerScreen;
