import { useEffect, useState, useRef } from "react";

// Î°úÍ∑∏Ïù∏Ïö©
import { useAgent } from "../../contexts/AgentContext";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../../firebase";
import { hashPassword } from "../../utils/hash";

import "../../styles/layout.css";
import "../../styles/transition.css";
import "./mainScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";
import Submits from "../../components/Submits";

function MainScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  // windowIndices: defaultWindowsÏùò Ïù∏Îç±Ïä§Îì§ÏùÑ Ï†ÄÏû• (Ïòà: [0, 1, 1, ...])
  const [windowIndices, setWindowIndices] = useState([]);
  const [clickDisabled, setClickDisabled] = useState(false);

  //Î°úÍ∑∏Ïù∏Ïö©
  const { setAgent } = useAgent();
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState("");

  // Î≤ÑÍ∑∏ Í∏ÄÏûê Î∞±Í∑∏ÎùºÏö¥Îìú
  const bugCount = 30;
  const bugs = Array.from({ length: bugCount }, (_, i) => {
    const top = Math.random() * 90; // % Í∏∞Ï§Ä
    const left = Math.random() * 90;
    const size = 1.5 + Math.random() * 1.5; // vh Îã®ÏúÑ Ìè∞Ìä∏ ÏÇ¨Ïù¥Ï¶à

    return (
      <span
        key={i}
        className="main-screen__bug-text"
        style={{
          position: "absolute",
          top: `${top}%`,
          left: `${left}%`,
          fontSize: `${size}vh`,
        }}
      >
        BUG
      </span>
    );
  });

  async function handleLoginSubmit(e) {
    e.preventDefault();
    const hashed = await hashPassword(password);

    const q = query(
      collection(db, "agents"),
      where("passwordHash", "==", hashed)
    );
    const snapshot = await getDocs(q);

    if (!snapshot.empty) {
      // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ
      const doc = snapshot.docs[0];
      const data = doc.data();
      setAgent({
        id: doc.id,
        progress: data.progress ?? 0,
        passwordHash: hashed,
      });
      setLoginError("");
      handleClick();
    } else {
      setLoginError("ÏöîÏõêÏΩîÎìúÍ∞Ä Ïò¨Î∞îÎ•¥ÏßÄ ÏïäÏäµÎãàÎã§.");
      handleClose();
    }
  }

  const screenRef = useRef(null);

  const se_error = new Audio(error);
  const se_shutdown = new Audio(shutdown);

  // Í∏∞Î≥∏ Ï∞Ω Î™©Î°ù (Ïù∏Îç±Ïä§ Í∏∞Î∞ò)
  const defaultWindows = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>ÏöîÏõêÏù¥Ïã≠ÎãàÍπå?</span>
          <div>
            <Buttons onClick={handleClick}>Ïòà</Buttons>
            <Buttons onClick={handleClose}>ÏïÑÎãàÏò§</Buttons>
          </div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>ÏöîÏõê ÏΩîÎìúÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.</span>
          <form onSubmit={handleLoginSubmit}>
            <input
              type="password"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
            <Submits value="Î°úÍ∑∏Ïù∏" />
          </form>
          {loginError && (
            <p style={{ color: "red", fontSize: "1.7vh" }}>{loginError}</p>
          )}
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <button className="vaccine-alert" onClick={handleClick}>
            <div className="vaccine-alert__start">START</div>
            <div className="vaccine-alert__vaccinate">VACCINATE</div>
          </button>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          Î∞±Ïã† ÌîÑÎ°úÍ∑∏Îû®ÏùÑ Í∞ÄÎèôÌï©ÎãàÎã§. <br />
          Í∞ïÏ†úÏ¢ÖÎ£åÌïòÏßÄ ÎßàÏÑ∏Ïöî.
          <br />
          (Ïû†Í∏àÌôîÎ©¥ Ìè¨Ìï®)
        </>
      ),
    },
  ];

  // Ï¥àÍ∏∞ fade-in Ìö®Í≥ºÏôÄ Ï≤´ Ï∞Ω(Ïù∏Îç±Ïä§ 0) ÌëúÏãú
  useEffect(() => {
    const fadeTimeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    return () => {
      clearTimeout(fadeTimeout);
    };
  }, []);

  // windowIndices ÏóÖÎç∞Ïù¥Ìä∏ Ïãú, ÎßàÏßÄÎßâ Ï∞Ω(Ïù∏Îç±Ïä§Í∞Ä defaultWindowsÏùò ÎßàÏßÄÎßâ)Ïóê ÎèÑÎã¨ÌïòÎ©¥ ÏûêÎèô ÏÇ≠Ï†ú ÏãúÌÄÄÏä§Î•º Ïã§Ìñâ
  useEffect(() => {
    if (
      !clickDisabled &&
      windowIndices.length > 0 &&
      windowIndices[windowIndices.length - 1] === defaultWindows.length - 1
    ) {
      setClickDisabled(true);
      se_error.play();
      setTimeout(() => {
        // ÎßàÏßÄÎßâ Ï∞ΩÎ∂ÄÌÑ∞ ÌïòÎÇòÏî© Ï†úÍ±∞ (Î∞∞Ïó¥Ïùò ÎßàÏßÄÎßâ ÏöîÏÜå Ï†úÍ±∞)
        setWindowIndices((prev) => prev.slice(0, -1));
        const removalInterval = setInterval(() => {
          setWindowIndices((prev) => {
            if (prev.length > 0) {
              return prev.slice(0, -1);
            } else {
              clearInterval(removalInterval);
              setTimeout(() => {
                se_shutdown.play();
                onNext && onNext();
              }, 300);
              return [];
            }
          });
        }, 50);
      }, 1000);
    }
  }, [windowIndices, clickDisabled, defaultWindows.length, onNext]);

  // handleClick: "Ïòà" Î≤ÑÌäº Îì±ÏóêÏÑú Ìò∏Ï∂ú, Ï∞Ω Ï∂îÍ∞Ä (ÎßàÏßÄÎßâ Ï∞ΩÏóê ÎèÑÎã¨ÌïòÎ©¥ ÏûêÎèô ÏÇ≠Ï†úÎ°ú Ï†ÑÌôò)
  function handleClick() {
    if (clickDisabled) return;
    const lastIndex = windowIndices[windowIndices.length - 1] ?? -1;
    if (lastIndex < defaultWindows.length - 1) {
      setWindowIndices((prev) => [...prev, lastIndex + 1]);
      se_error.play();
    }
  }

  // handleClose: x Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ìò∏Ï∂ú. Îã®, ÎßàÏßÄÎßâ Ï∞Ω(ÏµúÏÉÅÎã®)ÏóêÏÑúÎßå ÎèôÏûëÌïòÎèÑÎ°ù Ìï®.
  function handleClose() {
    if (clickDisabled) return;
    se_error.play();
    if (windowIndices.length > 0) {
      // ÎßàÏßÄÎßâ Ï∞ΩÏùò Ïù∏Îç±Ïä§Î•º Í∑∏ÎåÄÎ°ú Ï∂îÍ∞ÄÌïòÏó¨ Î≥µÏ†ú Ìö®Í≥ºÎ•º Ï§å.
      const lastIndex = windowIndices[windowIndices.length - 1];
      setWindowIndices((prev) => [...prev, lastIndex]);
    }
  }

  function handleFolderClick(index) {
    if (clickDisabled) return;
    setWindowIndices((prev) => [...prev, index]);
    se_error.play();
  }

  return (
    <div className={`mobile main-screen pre-fade ${fadeIn}`}>
      <div className="main-screen__bug-background">
        {bugs}
        <div className="main-screen__folders">
          {["ÌîÑÎ°úÍ∑∏Îû® ÏÑ§ÏπòÌååÏùº", "Î≥¥ÏïàÌå®Ïπò", "Î∞îÏù¥Îü¨Ïä§ ÏÉòÌîå", "ÎπÑÎ∞Ä Î°úÍ∑∏"].map(
            (label, index) => (
              <div key={index} className="folder-wrapper">
                <button
                  className="folder-button"
                  onClick={() => handleFolderClick(index)}
                >
                  üìÅ
                </button>
                <span>{label}</span>
              </div>
            )
          )}
        </div>
        <div className="main-screen__screen" ref={screenRef}>
          {windowIndices.map((idx, i) => {
            const win = defaultWindows[idx];
            if (!win) return null;
            const isLast = i === windowIndices.length - 1;
            return (
              <Window
                key={i}
                title={win.title}
                parentRef={screenRef}
                // onClose propÏùÄ Í∞ÄÏû• ÎßàÏßÄÎßâ Ï∞ΩÏóêÏÑúÎßå ÌôúÏÑ±ÌôîÎê®
                onClose={isLast ? handleClose : undefined}
                // active-window ÌÅ¥ÎûòÏä§Îäî CSSÏóêÏÑú Î≥ÑÎèÑÏùò Ïä§ÌÉÄÏùº(Ïòà: ÏÉâ Î≥ÄÍ≤Ω)ÏùÑ Ï§Ñ Ïàò ÏûàÏùå
                isActive={isLast ? "active" : "inactive"}
              >
                {win.content}
              </Window>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default MainScreen;
