import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  // 화면에 표시된 창 개수
  const [visibleCount, setVisibleCount] = useState(0);
  // 각 창의 클릭 가능 여부 (초기는 모두 true)
  const [windowClickable, setWindowClickable] = useState(Array(4).fill(true));
  // 각 창의 재마운트를 위한 key 배열
  const [windowKeys, setWindowKeys] = useState(Array(4).fill(0));
  const se_error = new Audio(error);

  // fadeIn 효과 설정
  useEffect(() => {
    const timeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    return () => clearTimeout(timeout);
  }, []);

  // 창 하나씩 추가 (visibleCount 증가)
  useEffect(() => {
    const timer = setTimeout(() => {
      // 새 창이 추가되면 이전 창은 클릭 불가로
      setWindowClickable((prev) => {
        const newArr = [...prev];
        for (let i = 0; i < visibleCount; i++) {
          newArr[i] = false;
        }
        newArr[visibleCount] = true;
        return newArr;
      });
      setVisibleCount(visibleCount + 1);
      se_error.play();
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  // 클릭할 때마다 한 개씩 창을 표시
  const handleClick = () => {
    if (visibleCount < windows.length - 1) {
      setWindowClickable((prev) => {
        const newArr = [...prev];
        // 이전창 비활성화
        for (let i = 0; i < visibleCount; i++) {
          newArr[i] = false;
        }
        newArr[visibleCount] = true;
        return newArr;
      });
      setVisibleCount(visibleCount + 1);
      se_error.play();
    } else {
      setVisibleCount(visibleCount + 1);
      se_error.play();

      // 1) 첫 번째 창만 천천히 제거
      setTimeout(() => {
        setVisibleCount((prevCount) => {
          // 혹시 아직 창이 남아있다면 하나 제거
          if (prevCount > 0) {
            se_error.play();
            return prevCount - 1;
          }
          return 0;
        });

        // 2) 그 다음부터는 빠른 간격으로 제거
        const removeInterval = setInterval(() => {
          setVisibleCount((prevCount) => {
            if (prevCount > 0) {
              //se_error.play();
              return prevCount - 1;
            } else {
              clearInterval(removeInterval);
              // 모든 창이 사라진 후 처리
              setTimeout(() => {
                const se_shutdown = new Audio(shutdown);
                se_shutdown.play();
                onNext && onNext();
              }, 300);
              return 0;
            }
          });
        }, 50); // 빠른 간격 (100ms)
      }, 1000); // 첫 번째 창 제거는 1초 후 (천천히)
    }
  };

  // x버튼이 눌렸을 때, 해당 창의 onClick 제거 & 강제로 재마운트하여 같은 내용의 창 띄우기
  const handleWindowClose = (index) => {
    se_error.play();
    setWindowClickable((prev) => {
      const newArr = [...prev];
      newArr[index] = false;
      return newArr;
    });
    setWindowKeys((prev) => {
      const newKeys = [...prev];
      newKeys[index] = newKeys[index] + 1;
      return newKeys;
    });
  };

  // 창 데이터 배열 (필요한 만큼 추가)
  const windows = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원이십니까?</span>
          <div>
            <Buttons onClick={handleClick}>예</Buttons>
            <Buttons>아니오</Buttons>
          </div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원 코드를 입력해주세요.</span>
          <input type="password" />
          <input type="submit" value="로그인" />
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <button className="vaccine-alert" onClick={handleClick}>
            <div className="vaccine-alert__start">START</div>
            <div className="vaccine-alert__vaccinate">VACCINATE</div>
          </button>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          백신 프로그램을 가동합니다. <br />
          강제종료하지 마세요.
          <br />
          (잠금화면 포함)
        </>
      ),
    },
  ];

  const screenRef = useRef(null);

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" ref={screenRef}>
        {windows.slice(0, visibleCount).map((win, index) => (
          <Window
            key={`${index}-${windowKeys[index]}`}
            title={win.title}
            parentRef={screenRef}
            onClose={() => handleWindowClose(index)}
          >
            {win.content}
          </Window>
        ))}
      </div>
    </div>
  );
}

export default ComputerScreen;
