import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

// Sound
import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  // activeWindows: 각 창 인스턴스를 객체로 관리
  const [activeWindows, setActiveWindows] = useState([
    { contentIndex: 0, clickable: true, key: Date.now() },
  ]);
  const se_error = new Audio(error);

  // fadeIn 효과
  useEffect(() => {
    const timeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);
    return () => clearTimeout(timeout);
  }, []);

  // 창 데이터 배열
  const windowsData = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원이십니까?</span>
          <div>
            <Buttons onClick={handleNormalClick}>예</Buttons>
            <Buttons onClick={handleNormalClick}>아니오</Buttons>
          </div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원 코드를 입력해주세요.</span>
          <input type="password" />
          <input type="submit" value="로그인" />
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <button className="vaccine-alert" onClick={handleNormalClick}>
            <div className="vaccine-alert__start">START</div>
            <div className="vaccine-alert__vaccinate">VACCINATE</div>
          </button>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          백신 프로그램을 가동합니다. <br />
          강제종료하지 마세요. <br />
          (잠금화면 포함)
        </>
      ),
    },
  ];

  // 일반 버튼 클릭 시 호출 – 새 창을 추가하는 함수
  function handleNormalClick() {
    const nextIndex = activeWindows.length; // 다음 창 내용 인덱스 (순서대로)
    if (nextIndex < windowsData.length) {
      addWindow(nextIndex);
    } else {
      onNext && onNext();
    }
  }

  // 새로운 창을 추가하면서, 기존 창들의 클릭을 모두 해제
  function addWindow(contentIndex) {
    setActiveWindows((prev) => {
      // 기존 창들 모두 클릭 불가
      const updated = prev.map((win) => ({ ...win, clickable: false }));
      // 새 창 추가 (클릭 가능)
      const newWindow = {
        contentIndex,
        clickable: true,
        key: Date.now() + Math.random(),
      };
      return [...updated, newWindow];
    });
    se_error.play();
  }

  // X 버튼 클릭 시 처리 – 해당 창은 그대로 유지하고, 같은 내용의 창을 duplicate하여 새로 추가
  function handleWindowClose(index) {
    se_error.play();
    setActiveWindows((prev) => {
      // 기존 모든 창은 클릭 불가로 설정
      const updated = prev.map((win) => ({ ...win, clickable: false }));
      // duplicate 창 생성: 동일한 contentIndex, 클릭 가능, 새로운 key
      const duplicate = {
        contentIndex: prev[index].contentIndex,
        clickable: true,
        key: Date.now() + Math.random(),
      };
      return [...updated, duplicate];
    });
  }

  const screenRef = useRef(null);

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" ref={screenRef}>
        {activeWindows.map((win, index) => (
          <Window
            key={`${win.key}`}
            title={windowsData[win.contentIndex].title}
            parentRef={screenRef}
            // onClick 전달은 클릭 가능할 때만
            onClick={win.clickable ? handleNormalClick : undefined}
            onClose={() => handleWindowClose(index)}
          >
            {windowsData[win.contentIndex].content}
          </Window>
        ))}
      </div>
    </div>
  );
}

export default ComputerScreen;
