import React from "react";
import Buttons from "../Buttons";
import Window from "../Window";

function QuizModal({
  level,
  question,
  hint,
  answer,
  value,
  onChange,
  onSubmit,
  onSkip,
  wrongAttempts,
  showHint,
  onClose,
  parentRef = {
    current: typeof document !== "undefined" ? document.body : null,
  },
}) {
  return (
    <div title="Quiz">
      <p>{question}</p>
      <input
        type="text"
        placeholder="정답을 입력하세요"
        value={value}
        onChange={(e) => {
          console.log(e);
          onChange(e.target.value);
        }}
        autoFocus
      />
      {wrongAttempts > 0 && <p style={{ color: "red" }}>틀렸습니다.</p>}
      {showHint && <p className="hint">힌트: {hint}</p>}
      <Buttons onClick={onSubmit}>제출</Buttons>
      {wrongAttempts >= 3 && <Buttons onClick={onSkip}>퀴즈 넘어가기</Buttons>}
    </div>
  );
}

export default QuizModal;
