import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";

// Sound
import error from "../../assets/sounds/error sound.mp3";

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  const [visibleCount, setVisibleCount] = useState(0);
  const [clickDisabled, setClickDisabled] = useState(false);
  const se_error = new Audio(error);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    return () => clearTimeout(timeout);
  }, []);

  // 창 데이터 배열 (필요한 만큼 추가)
  const windows = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          디버깅 요원이십니까? <br />예 / 아니오
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <div className="start-label">START</div>
          <div className="vaccinate-label">VACCINATE</div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          백신 프로그램을 가동합니다. <br />
          강제종료하지 마세요 (잠금화면 포함)
        </>
      ),
    },
  ];

  // 클릭할 때마다 한 개씩 창을 표시
  const handleClick = () => {
    if (visibleCount < windows.length) {
      setVisibleCount(visibleCount + 1);
      se_error.play();
    } else {
      setClickDisabled(true);
      // 모든 창이 뜨면 다음 화면 전환

      setTimeout(() => {
        setTimeout(() => {
          setVisibleCount(visibleCount - 1);
          setTimeout(() => {
            setVisibleCount(visibleCount - 1);
            setTimeout(() => {
              setVisibleCount(visibleCount - 1);
            }, 100);
          }, 100);
        }, 100);
        onNext();
      }, 100);
    }
  };

  const screenRef = useRef(null);

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" onClick={handleClick} ref={screenRef}>
        {windows.slice(0, visibleCount).map((win, index) => (
          <Window key={index} title={win.title} parentRef={screenRef}>
            {win.content}
          </Window>
        ))}
      </div>
    </div>
  );
}

export default ComputerScreen;
