import { useEffect, useState, useRef } from "react";
import "./minesweeper.css";
import quizzes from "../../assets/data/quizzes.json";

// MinesweeperÏóêÏÑú QuizModal Í¥ÄÎ†® Î°úÏßÅÏùÑ Ï†úÍ±∞ÌïòÍ≥†, onQuizOpen Ïù¥Î≤§Ìä∏Î•º Ìò∏Ï∂úÌïòÎèÑÎ°ù ÏàòÏ†ï
function Minesweeper({ level = 1, onCorrect, onSkip, onQuizOpen, solved }) {
  const [grid, setGrid] = useState([]);
  const [flagIndex, setFlagIndex] = useState(0);
  const [gameState, setGameState] = useState("cool");
  const [remainingMines, setRemainingMines] = useState(0);

  useEffect(() => {
    const size = level + 2;
    const newGrid = Array.from({ length: size }, () =>
      Array.from({ length: size }, () => ({
        revealed: false,
        flagged: false,
        cleared: false,
      }))
    );

    newGrid[0][0].flagged = true; // Ï≤´ ÍπÉÎ∞ú ÏÑ§Ï†ï
    setGrid(newGrid);
    setRemainingMines(1); // Ï¥àÍ∏∞ ÎÇ®ÏùÄ ÏßÄÎ¢∞ Í∞ØÏàò ÏÑ§Ï†ï
  }, [level]);

  const handleCellClick = (row, col) => {
    setGrid((prevGrid) => {
      const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
      const clicked = newGrid[row][col];
      if (clicked.flagged && !clicked.cleared) {
        onQuizOpen(level, `${row}-${col}`);
      }
      return newGrid;
    });
  };

  return (
    <div className="minesweeper">
      <div className="game-info">
        <span className="game-state">{gameState === "cool" ? "üòé" : gameState === "angry" ? "üò°" : "ü§î"}</span>
        <span className="remaining-mines">ÎÇ®ÏùÄ ÏßÄÎ¢∞: {remainingMines}</span>
      </div>
      <div className="minesweeper-grid" style={{ "--cols": grid.length }}>
        {grid.map((row, rowIndex) =>
          row.map((cell, colIndex) => (
            <div
              key={`${rowIndex}-${colIndex}`}
              className={`minesweeper-cell ${cell.flagged ? "flagged" : ""}`}
              onClick={() => handleCellClick(rowIndex, colIndex)}
            >
              {cell.flagged ? "üö©" : ""}
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Minesweeper;
