import { useRef, useState } from "react";

// screens
import BugFound from "./BugFound";
import ComputerScreen from "./ComputerScreen";
import StartScreen from "./StartScreen";

// styles
import "./styles/transition.css";

function App() {
  const [screen, setScreen] = useState("startScreen");
  const audioRef = useRef(null);

  let currentScreen;

  switch (screen) {
    case "startScreen":
      currentScreen = <StartScreen onNext={() => setScreen("bugFound")} />;
      break;
    case "bugFound":
      currentScreen = <BugFound onNext={() => setScreen("computerScreen")} />;
      break;
    case "computerScreen":
      currentScreen = <ComputerScreen onNext={() => setScreen("bugFound")} />;
      break;
    default:
      currentScreen = <div>404</div>;
  }

  return <div>{currentScreen}</div>;
}

export default App;
