import {
  collection,
  query,
  where,
  getDocs,
  doc,
  getDoc,
} from "firebase/firestore";
import { db } from "../../firebase";
import { useEffect, useState } from "react";
import Window from "../Window";
import Buttons from "../Buttons";
import "./minesweeper.css";
import quizData from "../../assets/data/quizzes.json";

function Minesweeper({ level = 1, onCorrect, onClose }) {
  const [grid, setGrid] = useState([]);
  const [mineCount, setMineCount] = useState(0);
  const [quizOpen, setQuizOpen] = useState(false);
  const [quizAnswer, setQuizAnswer] = useState("");
  const [wrongAttempts, setWrongAttempts] = useState(0);
  const [showHint, setShowHint] = useState(false);
  const [skippedFlags, setSkippedFlags] = useState([]);
  const [resetKey, setResetKey] = useState(0);
  const [gameState, setGameState] = useState("angry"); // "angry" or "cool"

  const handleSubmitanswer = async () => {
    const dirs = [-1, 0, 1];
    try {
      const data = quizData[level];
      if (data) {
        const correctAnswer = data.answer;

        if (quizAnswer.trim() === correctAnswer) {
          setGrid((prevGrid) => {
            const newGrid = prevGrid.map((row) => row.map((c) => ({ ...c })));

            // Reveal only one flagged mine and mark as cleared
            let found = false;
            for (let i = 0; i < newGrid.length; i++) {
              for (let j = 0; j < newGrid[i].length; j++) {
                if (newGrid[i][j].flagged && !newGrid[i][j].cleared) {
                  newGrid[i][j].cleared = true;
                  newGrid[i][j].revealed = true;
                  newGrid[i][j].flagged = false;

                  // Reveal surrounding numbers around this cleared flagged cell
                  dirs.forEach((dx) => {
                    dirs.forEach((dy) => {
                      if (dx === 0 && dy === 0) return;
                      const nx = i + dx;
                      const ny = j + dy;
                      if (
                        nx >= 0 &&
                        ny >= 0 &&
                        nx < newGrid.length &&
                        ny < newGrid[0].length &&
                        !newGrid[nx][ny].mine
                      ) {
                        newGrid[nx][ny].revealed = true;
                      }
                    });
                  });

                  // Find next mine to flag
                  let foundNext = false;
                  for (let x = 0; x < newGrid.length; x++) {
                    for (let y = 0; y < newGrid[x].length; y++) {
                      if (newGrid[x][y].mine && !newGrid[x][y].cleared) {
                        newGrid[x][y].flagged = true;
                        // reveal its adjacent numbers immediately
                        dirs.forEach((dx) => {
                          dirs.forEach((dy) => {
                            if (dx === 0 && dy === 0) return;
                            const nx = x + dx,
                              ny = y + dy;
                            if (
                              nx >= 0 &&
                              ny >= 0 &&
                              nx < newGrid.length &&
                              ny < newGrid[0].length &&
                              !newGrid[nx][ny].mine
                            ) {
                              newGrid[nx][ny].revealed = true;
                            }
                          });
                        });
                        foundNext = true;
                        break;
                      }
                    }
                    if (foundNext) break;
                  }

                  found = true;
                  break;
                }
              }
              if (found) break;
            }

            return newGrid;
          });

          setGameState((s) => {
            // if last mine, switch to cool
            const isLast = grid.every((row) =>
              row.every((c) => !c.mine || c.cleared)
            );
            return isLast ? "cool" : s;
          });

          setTimeout(() => {
            setGrid((prevGrid) => {
              const newGrid = prevGrid.map((row) => row.map((c) => ({ ...c })));
              for (let i = 0; i < newGrid.length; i++) {
                for (let j = 0; j < newGrid[i].length; j++) {
                  if (
                    newGrid[i][j].mine &&
                    !newGrid[i][j].cleared &&
                    !newGrid[i][j].flagged
                  ) {
                    newGrid[i][j].flagged = true;

                    // Reveal surrounding numbers
                    dirs.forEach((dx) => {
                      dirs.forEach((dy) => {
                        if (dx === 0 && dy === 0) return;
                        const nx = i + dx;
                        const ny = j + dy;
                        if (
                          nx >= 0 &&
                          ny >= 0 &&
                          nx < newGrid.length &&
                          ny < newGrid[0].length &&
                          !newGrid[nx][ny].mine
                        ) {
                          newGrid[nx][ny].revealed = true;
                        }
                      });
                    });

                    return newGrid;
                  }
                }
              }
              return newGrid;
            });
          }, 0);

          setQuizOpen(false);
          onCorrect && onCorrect();
        } else {
          setWrongAttempts((prev) => {
            const next = prev + 1;
            if (next >= 3) setShowHint(true);
            return next;
          });
          alert("ÌãÄÎ†∏ÏäµÎãàÎã§!");
        }
      } else {
        alert("Î¨∏Ï†ú Îç∞Ïù¥ÌÑ∞Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
      }
    } catch (error) {
      console.error("Ï†ïÎãµ ÌôïÏù∏ Ï§ë Ïò§Î•ò:", error);
    } finally {
      setQuizAnswer("");
    }
  };

  useEffect(() => {
    const size = 5 + level * 2; // grid size increases with level (7 to 13)
    const mines = level; // mine count equals level for now
    setMineCount(mines);

    const newGrid = Array.from({ length: size }, () =>
      Array.from({ length: size }, () => ({
        revealed: false,
        mine: false,
        flagged: false,
        cleared: false,
        adjacentMines: 0,
      }))
    );

    // Place mines randomly with one flagged
    const allCells = [];
    for (let i = 0; i < size; i++) {
      for (let j = 0; j < size; j++) {
        allCells.push([i, j]);
      }
    }

    const shuffled = allCells.sort(() => 0.5 - Math.random());
    for (let i = 0; i < mines; i++) {
      const [x, y] = shuffled[i];
      newGrid[x][y].mine = true;
    }

    // Randomly select one mine to be flagged and reveal surrounding numbers
    const [fx, fy] = shuffled[Math.floor(Math.random() * mines)];
    newGrid[fx][fy].flagged = true;
    const dirs = [-1, 0, 1];
    dirs.forEach((dx) => {
      dirs.forEach((dy) => {
        if (dx === 0 && dy === 0) return;
        const nx = fx + dx;
        const ny = fy + dy;
        if (
          nx >= 0 &&
          ny >= 0 &&
          nx < newGrid.length &&
          ny < newGrid[0].length &&
          !newGrid[nx][ny].mine
        ) {
          newGrid[nx][ny].revealed = true;
        }
      });
    });

    // Calculate adjacent mines
    const dirs2 = [-1, 0, 1];
    for (let x = 0; x < size; x++) {
      for (let y = 0; y < size; y++) {
        if (newGrid[x][y].mine) continue;
        let count = 0;
        dirs2.forEach((dx) => {
          dirs2.forEach((dy) => {
            if (dx === 0 && dy === 0) return;
            const nx = x + dx;
            const ny = y + dy;
            if (
              nx >= 0 &&
              ny >= 0 &&
              nx < size &&
              ny < size &&
              newGrid[nx][ny].mine
            ) {
              count++;
            }
          });
        });
        newGrid[x][y].adjacentMines = count;
      }
    }

    setGrid(newGrid);
  }, [level, resetKey]);

  const onCellClick = (row, col) => {
    setGrid((prevGrid) => {
      const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
      const clicked = newGrid[row][col];
      if (clicked.cleared) return newGrid;

      if (clicked.flagged && !clicked.cleared) {
        clicked.revealed = true;
        clicked.flagged = false;
        clicked.cleared = true;

        setQuizOpen(true); // Open quiz window

        // Removed activation of next flagged mine here per instructions
      } else if (!clicked.mine) {
        clicked.revealed = true;
      }

      return newGrid;
    });
  };

  return (
    <div className="minesweeper">
      <div
        className="minesweeper-center-icon"
        onClick={() => {
          if (gameState === "angry") {
            // reset game
            setResetKey((k) => k + 1);
            setWrongAttempts(0);
            setShowHint(false);
            setQuizOpen(false);
          } else {
            // close window
            onClose && onClose();
          }
        }}
      >
        {gameState === "angry" ? "üò°" : "üòé"}
      </div>
      <p>
        Level: {level}, Mines: {mineCount}
      </p>
      <div className="minesweeper-wrapper">
        <div
          className="minesweeper-grid"
          style={{ gridTemplateColumns: `repeat(${grid.length}, 1fr)` }}
        >
          {grid.map((row, rowIndex) => (
            <div key={rowIndex} className="minesweeper-row">
              {row.map((cell, colIndex) => (
                <div
                  key={`${rowIndex}-${colIndex}`}
                  className={`minesweeper-cell ${
                    cell.revealed ? "revealed" : ""
                  } ${cell.flagged ? "flagged" : ""}`}
                  onClick={() => onCellClick(rowIndex, colIndex)}
                >
                  {cell.revealed
                    ? cell.cleared
                      ? "üß™"
                      : cell.mine
                      ? "üí£"
                      : cell.adjacentMines > 0
                      ? cell.adjacentMines
                      : ""
                    : skippedFlags.includes(`${rowIndex}-${colIndex}`)
                    ? "‚ùå"
                    : cell.flagged
                    ? ""
                    : ""}
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
      {quizOpen && (
        <Window title="ÌÄ¥Ï¶à" onClose={() => setQuizOpen(false)}>
          <p>{quizData[level]?.question}</p>
          <input
            type="text"
            placeholder="Ï†ïÎãµÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            value={quizAnswer}
            onChange={(e) => setQuizAnswer(e.target.value)}
          />
          {showHint && <p className="hint">ÌûåÌä∏: {quizData[level]?.hint}</p>}
          <Buttons onClick={handleSubmitanswer}>Ï†úÏ∂ú</Buttons>
          {wrongAttempts >= 3 && (
            <Buttons
              onClick={() => {
                setQuizOpen(false);
                setGrid((prevGrid) => {
                  const newGrid = prevGrid.map((r) => r.map((c) => ({ ...c })));
                  for (let i = 0; i < newGrid.length; i++) {
                    for (let j = 0; j < newGrid[i].length; j++) {
                      const cell = newGrid[i][j];
                      if (cell.flagged && !cell.cleared) {
                        cell.revealed = true;
                        cell.flagged = false;
                        cell.cleared = true;
                        setSkippedFlags((prev) => [...prev, `${i}-${j}`]);
                        return newGrid;
                      }
                    }
                  }
                  return newGrid;
                });
              }}
            >
              ÌÄ¥Ï¶à ÎÑòÏñ¥Í∞ÄÍ∏∞
            </Buttons>
          )}
        </Window>
      )}
    </div>
  );
}

export default Minesweeper;
