import React, { useState } from "react";
import Buttons from "../Buttons";
import Window from "../Window";

function QuizModal({ level, quizData, onSubmit, onClose }) {
  const [quizAnswer, setQuizAnswer] = useState("");
  const [wrongAttempts, setWrongAttempts] = useState(0);
  const [showHint, setShowHint] = useState(false);

  const handleSubmit = () => {
    const correctAnswer = quizData[level]?.answer;
    if (quizAnswer.trim() === correctAnswer) {
      onSubmit(true); // Notify MainScreen of correct answer
      onClose();
    } else {
      setWrongAttempts((prev) => {
        const next = prev + 1;
        if (next >= 3) setShowHint(true);
        return next;
      });
      onSubmit(false); // Notify MainScreen of incorrect answer
    }
  };

  return (
    <Window title="Quiz" onClose={onClose}>
      <p>{quizData[level]?.question}</p>
      <input
        type="text"
        placeholder="정답을 입력하세요"
        value={quizAnswer}
        onChange={(e) => setQuizAnswer(e.target.value)}
      />
      {wrongAttempts > 0 && wrongAttempts < 3 && (
        <p style={{ color: "red" }}>틀렸습니다!</p>
      )}
      {showHint && <p className="hint">힌트: {quizData[level]?.hint}</p>}
      <Buttons onClick={handleSubmit}>제출</Buttons>
    </Window>
  );
}

export default QuizModal;
