//import styles from "./App.module.css";
import BugFound from "./BugFound";
import { useState } from "react";
import ComputerScreen from "./ComputerScreen";

// styles
import "./styles/transition.css";

function App() {
  const [screen, setScreen] = useState("bugFound");
  const [fadeIn, setFadeIn] = useState(true);

  const handleScreenChange = (nextScreen) => {
    setFadeIn(false); // 먼저 opacity 0
    setTimeout(() => {
      setScreen(nextScreen); // 실제 화면 전환
      setFadeIn(true); // 다시 fade-in
    }, 100); // 전환 딜레이 (0.1초)
  };

  switch (screen) {
    case "bugFound":
      return <BugFound onNext={() => handleScreenChange("computerScreen")} />;
    case "computerScreen":
      return <ComputerScreen onNext={() => setScreen("bugFound")} />;
  }
}

export default App;
