import { useEffect, useState, useRef } from "react";
import "../../styles/layout.css";
import "../../styles/transition.css";
import "./computerScreen.css";
import Window from "../../components/Window";
import Buttons from "../../components/Buttons";

import error from "../../assets/sounds/error sound.mp3";
import shutdown from "../../assets/sounds/shut down.mp3";

function ComputerScreen({ onNext }) {
  const [fadeIn, setFadeIn] = useState("");
  const [currentIndex, setCurrentIndex] = useState(-1); // 보여줄 최대 index
  const [clickDisabled, setClickDisabled] = useState(false);
  const screenRef = useRef(null);

  const se_error = new Audio(error);
  const se_shutdown = new Audio(shutdown);

  const defaultWindows = [
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원이십니까?</span>
          <div>
            <Buttons onClick={handleClick}>예</Buttons>
            <Buttons>아니오</Buttons>
          </div>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <span>요원 코드를 입력해주세요.</span>
          <input type="password" />
          <input type="submit" value="로그인" />
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          <button className="vaccine-alert" onClick={handleClick}>
            <div className="vaccine-alert__start">START</div>
            <div className="vaccine-alert__vaccinate">VACCINATE</div>
          </button>
        </>
      ),
    },
    {
      title: "UglyWorld in_BUG",
      content: (
        <>
          백신 프로그램을 가동합니다. <br />
          강제종료하지 마세요.
          <br />
          (잠금화면 포함)
        </>
      ),
    },
  ];

  // 초기 창 하나 보여주기
  useEffect(() => {
    const timeout = setTimeout(() => {
      setFadeIn("fade-in");
    }, 50);

    const timer = setTimeout(() => {
      setCurrentIndex(0);
      se_error.play();
    }, 1000);

    return () => {
      clearTimeout(timeout);
      clearTimeout(timer);
    };
  }, []);

  // "예" 버튼 누르면 창 하나씩 추가
  function handleClick() {
    if (clickDisabled) return;

    if (currentIndex < defaultWindows.length - 1) {
      setCurrentIndex((prev) => prev + 1);
      se_error.play();
    } else {
      // 마지막 창 도달 시: 자동 종료 시퀀스
      setClickDisabled(true);
      se_error.play();

      setTimeout(() => {
        setCurrentIndex((prev) => prev - 1);
        const interval = setInterval(() => {
          setCurrentIndex((prev) => {
            if (prev > 0) {
              return prev - 1;
            } else {
              clearInterval(interval);
              setTimeout(() => {
                se_shutdown.play();
                onNext && onNext();
              }, 300);
              return -1;
            }
          });
        }, 50);
      }, 1000);
    }
  }

  // x 버튼 누르면 같은 창 하나 더 띄우는 효과
  function handleClose() {
    se_error.play();
    if (currentIndex < defaultWindows.length - 1) {
      setCurrentIndex((prev) => prev + 1);
    }
  }

  return (
    <div className={`mobile computer-screen pre-fade ${fadeIn}`}>
      <div className="screen" ref={screenRef}>
        {defaultWindows.slice(0, currentIndex + 1).map((win, index) => (
          <Window
            key={index}
            title={win.title}
            parentRef={screenRef}
            onClose={handleClose}
          >
            {win.content}
          </Window>
        ))}
      </div>
    </div>
  );
}

export default ComputerScreen;
