import { useState, useRef } from "react";
import logo from "./assets/images/uglyworld_logo.png";
import "./styles/startScreen.css";
import bgm from "./assets/sounds/opening bgm.mp3";

function StartScreen({ onNext }) {
  const [clicked, setClicked] = useState(false);
  const audioRef = useRef(null);

  const handleClick = () => {
    console.log(clicked);
    if (clicked) return; // 중복 클릭 방지
    const se_bgm = new Audio(bgm);
    se_bgm.loop = true;
    se_bgm.play();
    audioRef.current = se_bgm;
    setClicked(true);
    console.log(clicked);
    setTimeout(() => {
      onNext();
    }, 600); // 애니메이션 끝난 뒤 전환
  };

  return (
    <div
      className={`mobile ${clicked ? "glitch-out" : ""}`}
      onClick={handleClick}
    >
      <div className="logo-wrapper">
        <img src={logo} className="logo" alt="uglyworld logo" />
      </div>
    </div>
  );
}

export default StartScreen;
