import BugFound from "./BugFound";
import ComputerScreen from "./ComputerScreen";
import { useState, useEffect } from "react";
import "./styles/transition.css";

function App() {
  const [screen, setScreen] = useState("bugFound");
  const [displayedScreen, setDisplayedScreen] = useState("bugFound");
  const [fadeClass, setFadeClass] = useState("fade-in");

  const handleScreenChange = (nextScreen) => {
    // 1. fade-out 시작
    setFadeClass("fade-out");

    // 2. fade-out 끝나고 컴포넌트 바꾸고 fade-in 시작
    setTimeout(() => {
      setDisplayedScreen(nextScreen);
      setFadeClass("fade-in");
      setScreen(nextScreen);
    }, 300); // fade-out 시간과 맞춤
  };

  let currentScreen;
  switch (displayedScreen) {
    case "bugFound":
      currentScreen = (
        <BugFound onNext={() => handleScreenChange("computerScreen")} />
      );
      break;
    case "computerScreen":
      currentScreen = (
        <ComputerScreen onNext={() => handleScreenChange("bugFound")} />
      );
      break;
    default:
      currentScreen = <div>404</div>;
  }

  return <div className={`mobile ${fadeClass}`}>{currentScreen}</div>;
}

export default App;
